Array.from(document.querySelectorAll('#post > p')).forEach(function (paragraph) {
    if (!paragraph.textContent.trim()) {
        for (var i = 0; i < paragraph.children.length; i++) if (paragraph.children[i].tagName !== "IMG") return;

        paragraph.classList.add('gallery');
    }
}), Array.from(document.querySelectorAll('#post > p img:not(.static)')).forEach(function (image) {
    var title = image.getAttribute('alt') ? image.getAttribute('alt') : document.querySelector('#post header h2').textContent;
    image.classList.add('glightbox'), image.setAttribute('data-gallery', 'gallery'), image.setAttribute('data-title', title);
});
var lightbox = GLightbox();
